sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;

	// Function called by clicking on a Note
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		// Emitting an event to Notes Body widget
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	// Catching update event from Notes Body Widget
	$rootScope.$on("noteUpdated", function(event, data){
		// Finding the note in data array
		var updatedNote = c.data.notes.find(function(note){
			return note.sys_id === data.noteID
		})
		// Updateing the title and body
		updatedNote.title = data.noteTitle
		updatedNote.note = data.noteBody
	})
}

sp_widget.script:
(function() {
	// Create an array to populate with notes
	data.notes = [];

	// Building the query
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	// Loop on all found records
	while (noteGR.next()) {
		var noteObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,20);
		//push the populated obj into the array
		data.notes.push(noteObj);
	}
})();
